package week1.day2;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int fact = 1;
		for (int i = 1; i <=n; i++) {
			fact=i*fact;
			// 1=1*1
			// 2*1
			 //3*2  4*6  5*24
			}
		System.out.println("the factorial for"+n+"is:" +fact);
		}

	

}
